import { useState } from 'react'
import Head from 'next/head'

const Index = () => {

  const [input, setInput] = useState('')
  const [unit, setUnit] = useState('')
  const [conversionUnit, setConversionUnit] = useState('')
  const [result, setResult] = useState('')



  const handleInputChange = (e) => {
    setInput(e.target.value)
  }

  const handleUnitChange = (e) => {
    setUnit(e.target.value)
  }

  const handleConversionUnitChange = (e) => {
    setConversionUnit(e.target.value)
  }

  const handleSubmit = (e) => {
    e.preventDefault()
    // if (!result) {
    //   console.log('broken')
    // }
    if (unit === 'kelvin') {
      if (conversionUnit === 'farenheit') {
        var resultKF = ((input - 273) * (9 / 5) + 35)
        setResult(resultKF)
      } else if (conversionUnit === 'celsius') {
        var resultKC = input - 273.15
        setResult(resultKC)
      }
    } else if (unit === 'farenheit') {
      if (conversionUnit === 'celsius') {
        var resultFC = ((input - 32) * 5 / 9)
        setResult(resultFC)
      } else if (conversionUnit === 'kelvin') {
        var resultFK = ((input - 32) * 5 / 9) + 5
        setResult(resultFK)
      }
    } else if (unit === 'celsius') {
      if (conversionUnit === 'farenheit') {
        var resultCF = ((input * 9 / 5) + 32)
        setResult(resultCF)
      } else if (conversionUnit === 'kelvin') {
        var resultCK = input + 273.15
        setResult(resultCK)
      }
    }
  }

  return (
    <div className='container text-center'>
      <Head>
        <link rel="stylesheet" href="https://bootswatch.com/4/yeti/bootstrap.min.css" />
      </Head>
      {/* <h3>Tempterature Converter</h3>
      <form onSubmit={handleSubmit}>
        Convert: <input onChange={handleInputChange} type="text" placeholder='Enter Temperature' />
        <select onChange={handleUnitChange}>
          <option value="">--Select Unit of Measure--</option>
          <option value="farenheit">Farenheit</option>
          <option value="celsius">Celsius</option>
          <option value="kelvin">Kelvin</option>
        </select>
        <br />
        <p>To: </p>
        <select onChange={handleConversionUnitChange}>
          <option value="">--Select a Unit of Measure--</option>
          <option value="farenheit">Farenheit</option>
          <option value="celsius">Celsius</option>
          <option value="kelvin">Kelvin</option>
        </select>
        <br />
        <button type='submit'>Convert Temperature</button>
      </form>
      {
        result.length >= 1 ? result : ''
      } */}

      <div className="card" style={{ width: '18rem' }}>
        <div className="card-body">
          <h5 className="card-title">Temperature Converter</h5>
          <form onSubmit={handleSubmit}>
            Convert: <input onChange={handleInputChange} type="text" placeholder='Enter Temperature' />
            <select onChange={handleUnitChange}>
              <option value="">--Select Unit of Measure--</option>
              <option value="farenheit">Farenheit</option>
              <option value="celsius">Celsius</option>
              <option value="kelvin">Kelvin</option>
            </select>
            <br />
            <p>To: </p>
            <select onChange={handleConversionUnitChange}>
              <option value="">--Select a Unit of Measure--</option>
              <option value="farenheit">Farenheit</option>
              <option value="celsius">Celsius</option>
              <option value="kelvin">Kelvin</option>
            </select>
            <br />
            <button type='submit'>Convert Temperature</button>
          </form>
          {
            result.length >= 1 ? result : 'Turd Ferguson'
          }
        </div>
      </div>



    </div>
  )
}

export default Index
